import uniqid from "uniqid"
import { useState } from "react"
import { useNavigate } from "react-router-dom"
import { useCVsContext } from "../hooks/useCVsContext"

const AddCV = () => {
  const { cvsDispatch } = useCVsContext()

  const [address, setAddress] = useState('')
  const [phone, setPhone] = useState('')
  const [email, setEmail] = useState('')

  const [title, setTitle] = useState('')
  const [name, setName] = useState('')
  const [profile, setProfile] = useState('')

  const [website, setWebsite] = useState('')
  const [github, setGithub] = useState('')
  const [linkedin, setLinkedin] = useState('')

  const [experience, setExperience] = useState([])
    const [jobtitle, setJobtitle] = useState('')
    const [companyname, setCompanyname] = useState('')
    const [highlights, setHighlights] = useState('')
    const [expdatefrom, setExpDatefrom] = useState('')
    const [expdateto, setExpDateto] = useState('')

  const [schools, setSchools] = useState([])
    const [schoolName, setSchoolName] = useState('')
    const [schoolLocation, setSchoolLocation] = useState('')
    const [schoolDegree, setSchoolDegree] = useState('')
    const [schdatefrom, setSchDatefrom] = useState('')
    const [schdateto, setSchDateto] = useState('')

  const [error, setError] = useState('')

  const navigate = useNavigate();

  const navigateDisplayPage = () => {
    navigate("/")
  }

  const handleAddJob = (e) => {
    e.preventDefault()

    setExperience([...experience, {jobtitle: jobtitle, companyname: companyname, highlights: highlights, datefrom: expdatefrom, dateto: expdateto}])
    setJobtitle('')
    setCompanyname('')
    setHighlights('')
    setExpDatefrom('')
    setExpDateto('')
  }

  const handleRemoveJob = (e) => {
    e.preventDefault()
    const remIndex = e.target.getAttribute("index")
    const newArr = experience

    const returnArr = newArr.splice(remIndex, newArr.length - remIndex)
    returnArr.shift()

    setExperience([...newArr, ...returnArr])
  }

  const handleAddSchool = (e) => {
    e.preventDefault()

    setSchools([...schools, {name: schoolName, location: schoolLocation, degree: schoolDegree, datefrom: schdatefrom, dateto: schdateto}])
    setSchoolName('')
    setSchoolLocation('')
    setSchoolDegree('')
    setSchDatefrom('')
    setSchDateto('')
  }

  const handleRemoveSchool = (e) => {
    e.preventDefault()
    const remIndex = e.target.getAttribute("index")
    const newArr = schools

    const returnArr = newArr.splice(remIndex, newArr.length - remIndex)
    returnArr.shift()

    setSchools([...newArr, ...returnArr])
  }

  const handleSubmit = async (e) => {
    e.preventDefault()

    const cv = {title, name, schools}

    const response = await fetch('/api/cvs', {
      method: 'POST',
      body: JSON.stringify(cv),
      headers: {
        'Content-Type': 'application/json'
      }
    })

    const json = await response.json

    if (!response.ok) {
      setError(json.error)
    }
    if (response.ok) {
      setTitle('')
      setName('')
      setSchools([])
      setError(null)

      cvsDispatch({type: 'CREATE_CV', payload: json})
      navigateDisplayPage()
    }
  }

  return ( 
    <form className="new-cv-form" onSubmit={handleSubmit}>
      <div className="add-title"><h3>Add a New CV</h3></div>
      <button>Add New CV</button>
      <div className="add-container">
        {error && <div className="form-error">{error}</div>}

        <input 
          type="text"
          onChange={(e) => setAddress(e.target.value)}
          value={address}
          placeholder="Your Address"
        />

        <input 
          type="tel"
          pattern="[0-9]{3}-[0-9]{3}-[0-9]{4}"
          onChange={(e) => setPhone(e.target.value)}
          value={phone}
          placeholder="555-555-5555"
        />

        <input 
          type="email"
          onChange={(e) => setEmail(e.target.value)}
          value={email}
          placeholder="your@email.com"
        />

        <input 
          type="text"
          onChange={(e) => setName(e.target.value)}
          value={name}
          placeholder="Your Name"
          required={true}
        />

        <input 
          type="text"
          onChange={(e) => setTitle(e.target.value)}
          value={title}
          placeholder="Your Professional Title"
        />

        <textarea 
          onChange={(e) => setProfile(e.target.value)}
          value={profile}
          placeholder="Include a short, general bio on yourself"
        />

        <hr />

        <div className="socials-entry">
          <input type="text" value={website} placeholder="Website Address" onChange={(e) => setWebsite(e.target.value)} />
          <input type="text" value={github} placeholder="Website Address" onChange={(e) => setGithub(e.target.value)} />
          <input type="text" value={linkedin} placeholder="Website Address" onChange={(e) => setLinkedin(e.target.value)} />
        </div>

        <hr />

        <div className="experience-entry">
          <input type="text" value={jobtitle} placeholder="Position Title" onChange={(e) => setJobtitle(e.target.value)} />
          <input type="text" value={companyname} placeholder="Company Name" onChange={(e) => setCompanyname(e.target.value)} />

          <div className="exp-duration-container">
            <input type="date" value={expdatefrom} placeholder="Degree Earned" onChange={(e) => setExpDatefrom(e.target.value)} />
            <input type="date" value={expdateto} placeholder="Degree Earned" onChange={(e) => setExpDateto(e.target.value)} />
          </div>

          <textarea value={highlights} placeholder="Highlight Responsibilities and Accomplishments" onChange={(e) => setHighlights(e.target.value)} />
          
          <button onClick={handleAddJob}>Add</button>
        </div>

        <div className="experience-entry-display">
          {experience && experience.map((job, i) => {
            return (
              <div key={uniqid()} className="job-container">
                <div>{job.jobtitle}</div>
                <span index={i} className="button-span" onClick={handleRemoveJob}>remove</span>
              </div>
            )
          })}
        </div>

        <hr />

        <div className="school-entry">
          <input type="text" value={schoolName} placeholder="School Name" onChange={(e) => setSchoolName(e.target.value)} />
          <input type="text" value={schoolLocation} placeholder="Location" onChange={(e) => setSchoolLocation(e.target.value)} />
          <input type="text" value={schoolDegree} placeholder="Degree Earned" onChange={(e) => setSchoolDegree(e.target.value)} />
          <div className="exp-duration-container">
            <input type="date" value={schdatefrom} placeholder="Degree Earned" onChange={(e) => setSchDatefrom(e.target.value)} />
            <input type="date" value={schdateto} placeholder="Degree Earned" onChange={(e) => setSchDateto(e.target.value)} />
          </div>
          <button onClick={handleAddSchool}>Add</button>
        </div>
        <div className="school-entry-display">
          {schools && schools.map((school, i) => {
            return (
              <div key={uniqid()} className="school-container">
                <div>{school.name}</div>
                <span index={i} className="button-span" onClick={handleRemoveSchool}>remove</span>
              </div>
            )
          })}
        </div>
      </div>
    </form>
  );
}
 
export default AddCV;
