import { createContext, useReducer } from 'react'

export const CVsContext = createContext()

export const cvsReducer = (state, action) => {
  switch (action.type) {
    case 'SET_CVS':
      return {
        cvs: action.payload
      }
    case 'CREATE_CV':
      return {
        cvs: [action.payload, ...state.cvs]
      }
    default:
      return state
  }
}

export const CVsContextProvider = ({ children }) => {
  const [state, dispatch] = useReducer(cvsReducer, {
    cvs: null
  })

  return ( 
    <CVsContext.Provider value={{...state, dispatch}}>
      { children }
    </CVsContext.Provider>
   );
}
