import { createContext, useReducer } from 'react'
import { useIndexContext } from '../hooks/useIndexContext'

export const CVsContext = createContext()

export const cvsReducer = (state, action) => {
  const { index } = useIndexContext()
  
  switch (action.type) {
    case 'SET_CVS':
      return {
        cvs: action.payload
      }
    case 'CREATE_CV':
      return {
        cvs: [action.payload, ...state.cvs]
      }
    case 'DELETE_CV':
      return {
        cvs: state.cvs.filter((cv) => cv._id !== action.payload._id)
      }
    case 'UPDATE_CV':
      let updatedArr = state

      updatedArr[index] = action.payload

      return {
        cvs: updatedArr
      }
    default:
      return state
  }
}

export const CVsContextProvider = ({ children }) => {
  const [state, cvsDispatch] = useReducer(cvsReducer, {
    cvs: null
  })

  return ( 
    <CVsContext.Provider value={{...state, cvsDispatch}}>
      { children }
    </CVsContext.Provider>
   );
}
