import { useEffect, useState } from "react"
import { useCVsContext } from "../hooks/useCVsContext"

// Components
import CVList from "../components/CVList"
import CVDisplay from "../components/CVDisplay"

const Home = () => {
  const {cvs, cvsDispatch} = useCVsContext()
  const {index, indexDispatch} = useState(0)

  useEffect(() => {
    const fetchCVs = async () => {
      const response = await fetch('/api/cvs')  // This fetch request will need to point explicitly to the full endpoint address (ie. 'https://mongodb.com/PORT:xxx')
      const json = await response.json()

      if (response.ok) {
        cvsDispatch({type: 'SET_CVS', payload: json})
      }
    }

    fetchCVs()
  }, [])

  const handleCVListIndex = (i) => {
    if (i>-1) setCVListIndex(i)
    else setCVListIndex(0)
  }

  return ( 
    <div className="home">
      <div className="cvs">
        {cvs && <CVList cvs={cvs} handleCVListIndex={handleCVListIndex}/>}
        {cvs && <CVDisplay cv={cvs[cvListIndex]} handleCVListIndex={handleCVListIndex} cvListIndex={cvListIndex} />}
      </div>
    </div>
   );
}
 
export default Home;
